    raiseIntakeButton.onTrue(new RaiseIntakeCommand(intakeArms).withInterruptBehavior(InterruptionBehavior.kCancelSelf));
    SmartDashboard.putData("RaiseIntakeButton", new RaiseIntakeCommand(intakeArms));

    final JoystickButton lowerIntakeButton = new JoystickButton(operatorController, XboxController.Button.kA.value);
    
    lowerIntakeButton.onTrue(new LowerIntakeCommand(intakeArms).withInterruptBehavior(InterruptionBehavior.kCancelSelf));
    SmartDashboard.putData("LowerIntakeButton", new LowerIntakeCommand(intakeArms));

    final Trigger forwardConveyorButton = new Trigger(() -> driveController.getRawAxis(XboxController.Axis.kRightTrigger.value) > RobotContainer.SHOOT_TRIGGER_THRESHOLD);
    forwardConveyorButton.whileTrue(new TurnConveyorCommand(0, conveyor));
    
    forwardConveyorButton.onTrue(new TurnConveyorCommand(0, conveyor).withInterruptBehavior(InterruptionBehavior.kCancelSelf));
    SmartDashboard.putData("ForwardConveyorButton", new TurnConveyorCommand(0, conveyor));

    final Trigger reverseConveyorButton = new Trigger(() -> driveController.getRawAxis(XboxController.Axis.kLeftTrigger.value) > RobotContainer.SHOOT_TRIGGER_THRESHOLD);
    forwardConveyorButton.whileTrue(new TurnConveyorCommand(0, conveyor));
    
